// 1. Try to find the text in several common n8n AI output paths
const rawText = $json.text || 
                $json.response?.generations?.[0]?.[0]?.text || 
                $json.output;

if (!rawText) {
  throw new Error("Could not find medical text in the input. Check the previous node's output.");
}

// 2. Clean out Markdown JSON wrappers (```json ... ```)
const cleanJson = rawText.replace(/```json/g, "").replace(/```/g, "").trim();

// 3. Return as a clean JSON object
try {
  return JSON.parse(cleanJson);
} catch (e) {
  return { error: "Failed to parse JSON", originalText: rawText };
}

// Format raw extraction for Draft (patient-friendly, document-specific only)
const raw = $json;
if (raw.error) return { json: { text: JSON.stringify(raw) } };
const prompt = `You are a medical assistant. Convert this raw medical extraction into a patient-friendly checklist. Rules:
- Use ONLY information from the extraction below. Do NOT add generic advice (e.g. no "watch for new symptoms" unless the note says so).
- Use 6th-grade reading level. Translate symbols like '<' and '>' into plain English (e.g. "worse when...", "better when...").
- If you see ICD-10-GM or OPS codes, give a brief plain-language explanation with the code in parentheses.
- Checklist: one item per diagnosis, one per medication (with dosage/frequency if present), one per warning or aggravating/relieving factor from the note. Be specific, not vague.
- audio_script: 2-3 short, warm sentences summarizing only what is in the extraction, for text-to-speech.

Raw extraction:
${JSON.stringify(raw, null, 2)}

Return ONLY valid JSON (no markdown, no backticks):
{"checklist":[{"text":"string","checked":true|false}],"audio_script":"string"}`;
return { json: { text: prompt } };

// Parse Draft output to checklist + audio_script
const rawText = $json.text || $json.response?.generations?.[0]?.[0]?.text || $json.output || '';
const clean = rawText.replace(/```json/g, '').replace(/```/g, '').trim();
let draft = { checklist: [], audio_script: '' };
try {
  draft = JSON.parse(clean);
} catch (e) {
  draft = { checklist: [{ text: 'Could not parse draft', checked: false }], audio_script: 'Please see the checklist for details.' };
}
if (!Array.isArray(draft.checklist)) draft.checklist = [];
if (!draft.audio_script) draft.audio_script = draft.checklist.map(c => c.text).join('. ') || 'See checklist.';
return { json: draft };

// Format prompt for Guardian (hallucination check)
const raw = $('Code in JavaScript').first().json;
const draft = $json;
if (raw.error || !draft.checklist) return { json: { text: 'Skip' } };
const prompt = `You are a medical safety auditor. Compare the ORIGINAL extraction with the DRAFT summary. Did the draft hallucinate (add meds, change dosages, add/remove diagnoses)? Return ONLY valid JSON: {"pass": true|false, "reason": "brief explanation"}

ORIGINAL:
${JSON.stringify(raw, null, 2)}

DRAFT:
${JSON.stringify(draft, null, 2)}`;
return { json: { text: prompt } };

// Parse Guardian output for pass/fail
const rawText = $json.text || $json.response?.generations?.[0]?.[0]?.text || $json.output || '';
const clean = rawText.replace(/```json/g, '').replace(/```/g, '').trim();
let result = { pass: true, reason: '' };
try {
  const parsed = JSON.parse(clean);
  result = { pass: parsed.pass === true, reason: parsed.reason || '' };
} catch (e) {}
return { json: result };

// Anonymize: strip PII before sending to Discord
const draft = $('Parse Draft').first().json;
const guardian = $('Parse Guardian').first().json;
const raw = $('Code in JavaScript').first().json;
const anonymized = {
  diagnosis: raw.Diagnosis || '(redacted)',
  medCount: Array.isArray(raw.Medications) ? raw.Medications.length : 0,
  warningCount: (raw.Warning_Signs || raw.WarningSigns || raw['Warning Signs'] || []).length,
  guardianFail: guardian.pass === false,
  reason: guardian.reason || ''
};
return { json: anonymized };

