const fallback = { summary: 'See checklist.', checklist: [{ text: 'No items', checked: false }], audio_base64: null, verifiedSafe: false };
try {
  let draft = { checklist: [], audio_script: '' };
  let guardian = { pass: true, reason: '' };
  try { draft = $('Parse Draft').first().json || draft; } catch (_) {}
  try { guardian = $('Parse Guardian').first().json || guardian; } catch (_) {}
  const checklist = Array.isArray(draft.checklist) ? draft.checklist : [{ text: 'No items', checked: false }];
  const summary = (draft.checklist && draft.checklist[0] && draft.checklist[0].text) ? draft.checklist[0].text : 'See checklist.';
  let audio_base64 = null;
  try {
    const buffer = await this.helpers.getBinaryDataBuffer(0, 'audio');
    if (buffer && buffer.length) audio_base64 = buffer.toString('base64');
  } catch (_) {}
  const verifiedSafe = guardian.pass === true;
  return { json: { summary, checklist, audio_base64, verifiedSafe } };
} catch (e) {
  return { json: { ...fallback, summary: 'Processing error' } };
}

// Get the image from webhook form data
const imageFile = $input.item.binary.image || $input.item.binary.data;

if (!imageFile) {
  throw new Error('No image found in webhook data');
}

// Pass through the binary data
return {
  json: {},
  binary: {
    data: imageFile
  }
};
